{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": 12
      },
      "commands": [
        "cd step14_CI_CD_pipeline",
        "cd step02_CI_CD_pipeline_update_cdk_template",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npm run cdk synth -- -o dist"
      ]
    }
  },
  "artifacts": {
    "base-directory": "./step02_CI_CD_pipeline_update_cdk_template/dist",
    "files": [
      "Step02CiCdPipelineUpdateCdkTemplateStack.template.json"
    ]
  }
}
